import { useState } from "react";

function App() {
  return (
    <div className="App">
      <MainHeader>
        <Logo></Logo>
        <Heading></Heading>
        <Search></Search>
        <LangSelector> </LangSelector>
      </MainHeader>
    </div>
  );
}

export default App;

function MainHeader({ children }) {
  return <nav className="header">{children}</nav>;
}

function Logo() {
  return <img src="1.png" alt=""></img>;
}

function Heading() {
  return (
    <header className="">
      <h1 style={{ color: "white" }}>ShopHub•</h1>
    </header>
  );
}

function Search() {
  const [search, setSearch] = useState("");
  return (
    <input
      className="search"
      type="text"
      placeholder="Search here...."
      value={search}
      onChange={(e) => setSearch(e.target.value)}
    ></input>
  );
}

function LangSelector() {
  return (
    <div>
      <label for="languageDropdown">Language:</label>
      <select id="languageDropdown" onchange="changeLanguage(this.value)">
        <option value="en">English</option>
        <option value="es">Español</option>
        <option value="fr">Français</option>
      </select>
    </div>
  );
}
