import { useState } from "react";
import image from "./logo.png";
import cart from "./cart.png";
function App() {
  return (
    <div className="App">
      <MainHeader>
        <Logo></Logo>
        <Heading></Heading>
        <Search></Search>
        <LangSelector> </LangSelector>
        <SignInPortal />
        <ShoppingCart />
      </MainHeader>
    </div>
  );
}

export default App;

function MainHeader({ children }) {
  return <nav className="header">{children}</nav>;
}

function Logo() {
  return (
    <span className="logo">
      <img src={image} alt=""></img>
    </span>
  );
}

function Heading() {
  return (
    <header className="">
      <h1 style={{ color: "white" }}>ShopHub•</h1>
      <button
        id="productbtn"
        style={{
          fontSize: "17px",
          position: "absolute",
          right: "20rem",
          top: "1.45rem",
          fontWeight: "initial",
          fontFamily: ` -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen,
          Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
      `,
        }}
      >
        Products
      </button>
    </header>
  );
}

function Search() {
  const [search, setSearch] = useState("");
  return (
    <input
      className="search"
      type="text"
      placeholder="Search here...."
      value={search}
      onChange={(e) => setSearch(e.target.value)}
    ></input>
  );
}

function LangSelector() {
  const [lang, setLang] = useState("");
  return (
    <div className="language">
      <label for="languageDropdown"></label>
      <select
        id="languageDropdown"
        value={lang}
        onChange={(e) => setLang(e.target.value)}
      >
        <option value="en">English</option>
        <option value="es">Español</option>
        <option value="fr">Français</option>
        <option value="gr">German</option>
        <option value="mx">Mexican</option>
        <option value="du">Dutch</option>
        <option value="ru">Russian</option>
      </select>
    </div>
  );
}

function SignInPortal() {
  return (
    <div className="signin">
      <button id="signinbtn">Sign in/Member</button>
    </div>
  );
}

function ShoppingCart() {
  return (
    <a href="">
      <img id="cart" src={cart} alt="shopping cart"></img>;
    </a>
  );
}
